import 'package:audio_book_flutter/data/model/book_model.dart';import 'package:audio_book_flutter/data/repository/app_repository_impl.dart';import 'package:audio_book_flutter/domain/repository/app_repository.dart';import 'package:bloc/bloc.dart';import 'package:meta/meta.dart';part 'by_category_event.dart';part 'by_category_state.dart';class ByCategoryBloc extends Bloc<ByCategoryEvent, ByCategoryState> {  final AppRepository _repository = AppRepositoryImpl();  ByCategoryBloc() : super(ByCategoryInitial()) {    on<GetBooksByCategoryEvent>((event, emit) async {      try {        List<BookData> books =            await _repository.getBooksByCategory(event.category);        emit(GetBooksByCategoryState(books));      } catch (e) {      }    });    on<BookToPlayEvent>((event, emit) {      emit(BookToPlayState(event.id));    });  }}