import 'package:flutter/material.dart';Widget authButton(String title, void Function() onClick) {  return GestureDetector(    onTap: onClick,    child: Container(      decoration: ShapeDecoration(        color: const Color(0xFFF26B6C),        shape: RoundedRectangleBorder(          borderRadius: BorderRadius.circular(200),        ),      ),      child: Padding(        padding: const EdgeInsets.symmetric(horizontal: 77, vertical: 12),        child: Text(          title,          textAlign: TextAlign.center,          style: const TextStyle(            color: Colors.white,            fontSize: 14,            fontFamily: 'Uni Neue',            fontWeight: FontWeight.w700,            height: 0,          ),        ),      ),    ),  );}