import 'package:flutter/material.dart';Widget getStartedButton(void Function() onClick) {  return GestureDetector(      onTap: onClick,      child: Container(      decoration: const BoxDecoration(          color: Color(0xFFF26B6C),          borderRadius: BorderRadius.all(Radius.circular(200))),      child: const FittedBox(        child: Padding(          padding: EdgeInsets.symmetric(horizontal: 35.0, vertical: 8),          child: Row(            children: [              Text(                'Get Started',                style: TextStyle(                  color: Colors.white,                  fontSize: 20,                  fontFamily: 'Uni Neue',                  fontWeight: FontWeight.w700,                  height: 0,                ),              ),              SizedBox(                width: 10,              ),              Icon(                Icons.arrow_forward,                color: Colors.white,              )            ],          ),        ),      )));}