import 'package:advance_pdf_viewer2/advance_pdf_viewer.dart';import 'package:firebase_storage/firebase_storage.dart';import 'package:flutter/material.dart';class DocumentViewerPage extends StatefulWidget {  final String book;  const DocumentViewerPage({super.key, required this.book});  @override  State<DocumentViewerPage> createState() => _DocumentViewerPageState();}class _DocumentViewerPageState extends State<DocumentViewerPage> {  PDFDocument? document;  bool _isLoading = true;  Future<void> loadDocument() async {    try {      final ref = FirebaseStorage.instance          .ref("${widget.book.split('/')[3]}/${widget.book.split('/')[4]}");      final url = await ref.getDownloadURL();      document = await PDFDocument.fromURL(url);    } catch (e) {      print('Error loading document: $e');    }    if (mounted) {      setState(() => _isLoading = false);    }  }  @override  void initState() {    super.initState();    loadDocument();  }  @override  Widget build(BuildContext context) {    return Scaffold(      body: _isLoading          ? Center(child: CircularProgressIndicator())          : document != null              ? PDFViewer(document: document!)              : Center(child: Text('Failed to load document')),    );  }}